// Enhanced JSON Parser with Better Error Handling
let input = $input.first().json.output;

function cleanAndParseJSON(rawInput) {
    try {
        // Step 1: Remove markdown code blocks
        let cleaned = rawInput.replace(/```json\s*/g, "").replace(/```\s*/g, "");

        // Step 2: Remove any leading/trailing text before first { and after last }
        let firstBrace = cleaned.indexOf("{");
        let lastBrace = cleaned.lastIndexOf("}");

        if (firstBrace === -1 || lastBrace === -1) {
            throw new Error("No JSON object found in response");
        }

        cleaned = cleaned.substring(firstBrace, lastBrace + 1);

        // Step 3: Unescape common escape sequences
        cleaned = cleaned.replace(/\\n/g, " ")
                       .replace(/\\r/g, " ")
                       .replace(/\\t/g, " ")
                       .replace(/\\"/g, '"');

        // Step 4: Fix common AI mistakes
        // Fix unquoted property names
        cleaned = cleaned.replace(/([{,]\s*)([a-zA-Z_$][a-zA-Z0-9_$]*)\s*:/g, '$1"$2":');

        // Remove trailing commas
        cleaned = cleaned.replace(/,\s*([}\]])/g, '$1');

        // Fix single quotes to double quotes (carefully)
        cleaned = cleaned.replace(/:\s*'([^']*)'/g, ': "$1"');

        // Step 5: Balance braces if truncated
        let openBraces = (cleaned.match(/{/g) || []).length;
        let closeBraces = (cleaned.match(/}/g) || []).length;
        if (closeBraces < openBraces) {
            cleaned += "}".repeat(openBraces - closeBraces);
        }

        let openBrackets = (cleaned.match(/\[/g) || []).length;
        let closeBrackets = (cleaned.match(/\]/g) || []).length;
        if (closeBrackets < openBrackets) {
            cleaned += "]".repeat(openBrackets - closeBrackets);
        }

        // Step 6: Attempt to parse
        return JSON.parse(cleaned);

    } catch (error) {
        throw new Error(`JSON parsing failed: ${error.message}`);
    }
}

function validateTestCaseStructure(parsed) {
    // Validate required top-level fields
    if (!parsed.automationType) {
        throw new Error("Missing 'automationType' field");
    }

    if (!parsed.requiredFields || typeof parsed.requiredFields !== 'object') {
        throw new Error("Missing or invalid 'requiredFields' object");
    }

    if (!parsed.cases || typeof parsed.cases !== 'object') {
        throw new Error("Missing or invalid 'cases' object");
    }

    // Validate all 4 cases exist
    const requiredCases = ['case_1', 'case_2', 'case_3', 'case_4'];
    for (const caseName of requiredCases) {
        if (!parsed.cases[caseName]) {
            throw new Error(`Missing ${caseName}`);
        }

        const testCase = parsed.cases[caseName];
        if (!testCase.description || !testCase.data) {
            throw new Error(`${caseName} missing 'description' or 'data'`);
        }
    }

    return true;
}

try {
    // Parse the JSON
    const parsedJSON = cleanAndParseJSON(input);

    // Validate structure
    validateTestCaseStructure(parsedJSON);

    // Return successfully parsed and validated JSON
    return [{
        json: {
            success: true,
            testCases: parsedJSON,
            metadata: {
                automationType: parsedJSON.automationType,
                fieldCount: Object.keys(parsedJSON.requiredFields).length,
                caseCount: Object.keys(parsedJSON.cases).length,
                timestamp: new Date().toISOString()
            }
        }
    }];

} catch (error) {
    // Return detailed error information
    return [{
        json: {
            success: false,
            error: error.message,
            rawOutput: input.substring(0, 500),
            suggestion: "The AI response was not in the expected format. Try re-running the workflow or check the system prompt."
        }
    }];
}

