function replaceTriggersWithWebhook(workflow) {
  const nodes = workflow.nodes;
  const connections = workflow.connections;

  // ðŸ“Œ List of all known trigger node types in n8n
  const TRIGGER_NODE_TYPES = [
    "n8n-nodes-base.manualTrigger",
    "n8n-nodes-base.cron",
    "n8n-nodes-base.schedule",
    "n8n-nodes-base.interval",
    "n8n-nodes-base.webhook",
    "n8n-nodes-base.respondToWebhook",
    "n8n-nodes-base.waitForWebhook",
    "n8n-nodes-base.fileTrigger",
    "n8n-nodes-base.s3Trigger",
    "n8n-nodes-base.googleDriveTrigger",
    "n8n-nodes-base.emailTrigger",
    "n8n-nodes-base.gmailTrigger",
    "n8n-nodes-base.microsoftOutlookTrigger",
    "n8n-nodes-base.rabbitMqTrigger",
    "n8n-nodes-base.redisTrigger",
    "n8n-nodes-base.kafkaTrigger",
    "n8n-nodes-base.sqsTrigger",
    "n8n-nodes-base.postgresTrigger",
    "n8n-nodes-base.mySqlTrigger",
    "n8n-nodes-base.mongoDbTrigger",
    "n8n-nodes-base.slackTrigger",
    "n8n-nodes-base.discordTrigger",
    "n8n-nodes-base.telegramTrigger",
    "n8n-nodes-base.microsoftTeamsTrigger",
    "n8n-nodes-base.whatsAppTrigger",
    "n8n-nodes-base.airtableTrigger",
    "n8n-nodes-base.notionTrigger",
    "n8n-nodes-base.googleSheetsTrigger",
    "n8n-nodes-base.trelloTrigger",
    "n8n-nodes-base.twitterTrigger",
    "n8n-nodes-base.facebookTrigger",
    "n8n-nodes-base.instagramTrigger",
    "n8n-nodes-base.linkedinTrigger",
    "n8n-nodes-base.shopifyTrigger",
    "n8n-nodes-base.wooCommerceTrigger",
    "n8n-nodes-base.stripeTrigger",
    "n8n-nodes-base.paypalTrigger",
    "n8n-nodes-base.githubTrigger",
    "n8n-nodes-base.gitlabTrigger",
    "n8n-nodes-base.jiraTrigger",
    "n8n-nodes-base.zapierTrigger",
    "n8n-nodes-base.formTrigger",
    "n8n-nodes-base.googleFormsTrigger",
    "n8n-nodes-base.typeformTrigger"
  ];

  // 1ï¸âƒ£ Skip if a webhook trigger already exists
  if (nodes.some(n => n.type === "n8n-nodes-base.webhook")) {
    console.log("Workflow already contains a webhook trigger. No changes made.");
    return workflow;
  }

  // 2ï¸âƒ£ Find all trigger nodes except existing webhook triggers
  const triggerNodes = nodes.filter(
    n => TRIGGER_NODE_TYPES.includes(n.type) && n.type !== "n8n-nodes-base.webhook"
  );

  if (triggerNodes.length === 0) {
    console.log("No trigger nodes to replace.");
    return workflow;
  }

  const basePos = triggerNodes[0].position || [200, 200];

  // 3ï¸âƒ£ Collect downstream connections of triggers
  const downstreamConnections = [];
  for (const [source, conn] of Object.entries(connections)) {
    if (triggerNodes.some(n => n.name === source)) {
      const outs = conn.main?.[0] || [];
      downstreamConnections.push(...outs);
      delete connections[source];
    }
  }

  // 4ï¸âƒ£ Create Webhook node
  const webhookNode = {
    id: "webhook_1",
    name: "Webhook Trigger",
    type: "n8n-nodes-base.webhook",
    typeVersion: 1,
    position: [basePos[0], basePos[1]],
    parameters: {
      httpMethod: "POST",
      path: "ai-workflow-test",
    },
  };

  // 5ï¸âƒ£ Prepare example values to mimic trigger output
  const exampleValues = [];
  const triggerData = triggerNodes[0]?.parameters || {};
  for (const key in triggerData) {
    exampleValues.push({ name: key, value: triggerData[key] });
  }
  if (exampleValues.length === 0) {
    exampleValues.push({ name: "exampleField", value: "example value" });
    exampleValues.push({ name: "source", value: "webhook-test" });
  }

  // 6ï¸âƒ£ Create Set node
  const setNode = {
    id: "set_1",
    name: "Set Sample Data",
    type: "n8n-nodes-base.set",
    typeVersion: 1,
    position: [basePos[0] + 250, basePos[1]],
    parameters: {
      values: {
        string: exampleValues,
      },
    },
  };

  // 7ï¸âƒ£ Remove trigger nodes
  const updatedNodes = nodes.filter(n => !triggerNodes.includes(n));

  // 8ï¸âƒ£ Add new Webhook and Set nodes
  updatedNodes.push(webhookNode, setNode);

  // 9ï¸âƒ£ Reconnect: Webhook â†’ Set node
  connections["Webhook Trigger"] = {
    main: [[{ node: "Set Sample Data", type: "main", index: 0 }]],
  };

  // ðŸ”Ÿ Reconnect: Set node â†’ previous trigger downstream nodes
  connections["Set Sample Data"] = {
    main: [downstreamConnections],
  };

  return {
    ...workflow,
    nodes: updatedNodes,
    connections,
  };
}

