  workflow = JSON.parse(workflowStr);
} catch (e) {
  return [{ json: { error: "Invalid workflow JSON" } }];
}

if (!workflow || !workflow.nodes) {
  return [{ json: { error: "Invalid workflow input" } }];
}

try {
  const updated = replaceTriggersWithWebhook(workflow);
  return [
    {
      json: {
        status: "success",
        updated_workflow: updated,
      },
    },
  ];
} catch (err) {
  return [
    {
      json: {
        status: "error",
        message: err.message,
      },
    },
  ];
}

