// --- MAIN ---
const workflowStr = $input.first().json.n8n_json;
let workflow;

try {
  workflow = JSON.parse(workflowStr);
} catch (e) {
  return [{ json: { error: "Invalid workflow JSON" } }];
}

if (!workflow || !workflow.nodes) {
  return [{ json: { error: "Invalid workflow input" } }];
}

// Check if any node is deactivated
const hasDeactivatedNodes = workflow.nodes.some(n => n.disabled === true);

return [
  {
    json: {
      hasDeactivatedNodes: hasDeactivatedNodes
    }
  }
];

const status = $input.all().find(i => i.json.status !== undefined)?.json.status || null;
const updated_workflow = $input.all().find(i => i.json.updated_workflow !== undefined)?.json.updated_workflow || null;
const hasDeactivatedNodes = $input.all().find(i => i.json.hasDeactivatedNodes !== undefined)?.json.hasDeactivatedNodes || false;

return [
  {
    json: {
      status,
      updated_workflow,
      hasDeactivatedNodes
    }
  }
];
