// ULTRA-ROBUST JSON PARSER for DeepSeek model output
const input = $input.first().json;

// Step 0: Extract raw text from DeepSeek response format
let raw = null;

// DeepSeek returns array of objects with "text" field
if (Array.isArray(input) && input[0]?.text) {
    raw = input[0].text;
} else if (input?.text) {
    raw = input.text;
} else {
    throw new Error("‚ùå No response text found from DeepSeek model");
}

if (!raw) throw new Error("‚ùå Empty response from DeepSeek model");

// Step 1: Clean markdown and whitespace
raw = raw
  .replace(/```json\s*/gi, '')
  .replace(/```\s*/g, '')
  .replace(/^[^{]*/, '')  // Remove text before first {
  .replace(/[^}]*$/, '')  // Remove text after last }
  .trim();

// Step 2: Extract JSON object
const jsonStart = raw.indexOf('{');
const jsonEnd = raw.lastIndexOf('}');
if (jsonStart === -1 || jsonEnd === -1) throw new Error("‚ùå No JSON object found");
raw = raw.substring(jsonStart, jsonEnd + 1);

// Step 3: Advanced repair function
function repairJSON(str) {
  let repaired = str;
  repaired = repaired.replace(/,\s*([\]}])/g, '$1'); // remove trailing commas
  repaired = repaired.replace(/\}\s*\{/g, '},{'); // fix merged objects
  repaired = repaired.replace(/\]\s*\[/g, '],['); // fix merged arrays
  repaired = repaired.replace(/"([^"]*?)"([^,:{}\[\]]*?)"([^"]*?)"/g, (m,p1,p2,p3) => p2 && !p2.match(/^\s*[,:\]}]\s*$/) ? `"${p1}${p2.replace(/"/g,'\\"')}${p3}"` : m);
  repaired = repaired.replace(/[\x00-\x1F\x7F]/g,''); // remove invalid characters
  repaired = repaired.replace(/([{,]\s*)([a-zA-Z_][a-zA-Z0-9_]*)\s*:/g,'$1"$2":'); // quote keys
  repaired = repaired.replace(/([\[{,])\s+/g,'$1'); // remove spaces after brackets
  repaired = repaired.replace(/\s+([\]}])/g,'$1'); // remove spaces before brackets
  return repaired;
}

// Step 4: Attempt multiple parses
let parsed = null;
let parseAttempts = [
  () => JSON.parse(raw),
  () => JSON.parse(repairJSON(raw)),
  () => {
    const workflowMatch = raw.match(/"modified_workflow"\s*:\s*\{[\s\S]*?\}\s*,\s*"added_webhook_path"\s*:\s*".*?"\s*,\s*"n8n_workflow_name"\s*:\s*".*?"/i);
    if (workflowMatch) return JSON.parse(repairJSON('{' + workflowMatch[0] + '}'));
    throw new Error("Cannot extract main workflow fields");
  }
];

let lastError = null;
for (let i = 0; i < parseAttempts.length; i++) {
  try {
    parsed = parseAttempts[i]();
    break;
  } catch(e) {
    lastError = e;
  }
}

if (!parsed) throw new Error(`‚ùå All parse attempts failed. Last error: ${lastError.message}`);

// Step 5: Validate required fields
const requiredFields = ["modified_workflow","added_webhook_path","n8n_workflow_name"];
for (const f of requiredFields) {
  if (!(f in parsed)) throw new Error(`‚ùå Missing field: ${f}`);
}

// Step 6: Return parsed JSON in system message format
return [{
  json: {
    modified_workflow: parsed.modified_workflow,
    added_webhook_path: parsed.added_webhook_path,
    n8n_workflow_name: parsed.n8n_workflow_name
  }
}];

// Advanced workflow validation with auto-fix
const nodes =$input.first().json.modified_workflow.nodes;
const connections = $input.first().json.modified_workflow.connections;
const webhook_path = $input.first().json.added_webhook_path;

console.log(`üîç Validating ${nodes.length} nodes...`);

// 1. Validate and fix all nodes
const validatedNodes = [];
const nodeIds = new Set();
const nodeNames = new Set();
let fixedCount = 0;

for (let i = 0; i < nodes.length; i++) {
    const node = nodes[i];
    let fixed = false;

    // Fix missing ID
    if (!node.id) {
        node.id = `node-${Date.now()}-${i}-${Math.random().toString(36).substr(2, 9)}`;
        fixed = true;
    }

    // Fix missing name
    if (!node.name) {
        node.name = `Node_${i + 1}_${node.type?.split('.').pop() || 'Unknown'}`;
        fixed = true;
    }

    // Check for duplicate names
    if (nodeNames.has(node.name)) {
        node.name = `${node.name}_${Date.now()}_${i}`;
        fixed = true;
    }

    // Fix missing type
    if (!node.type) {
        console.log(`‚ùå Node ${node.name} has no type - skipping`);
        continue;
    }

    // Fix missing position
    if (!node.position || !Array.isArray(node.position) || node.position.length !== 2) {
        node.position = [i * 240, 1824];
        fixed = true;
    }

    // Fix invalid position values
    if (typeof node.position[0] !== 'number' || typeof node.position[1] !== 'number') {
        node.position = [i * 240, 1824];
        fixed = true;
    }

    // Fix missing typeVersion
    if (!node.typeVersion) {
        node.typeVersion = 1;
        fixed = true;
    }

    // Ensure parameters exist
    if (!node.parameters) {
        node.parameters = {};
        fixed = true;
    }

    if (fixed) {
        fixedCount++;
        console.log(`üîß Fixed node: ${node.name}`);
    }

    nodeIds.add(node.id);
    nodeNames.add(node.name);
    validatedNodes.push(node);
}

console.log(`‚úÖ Validated ${validatedNodes.length} nodes (${fixedCount} fixed)`);

if (validatedNodes.length === 0) {
    throw new Error("‚ùå No valid nodes after validation");
}

// 2. Clean and validate connections
const cleanedConnections = {};
let removedConnections = 0;
let fixedConnections = 0;

for (const [sourceNodeName, connectionTypes] of Object.entries(connections)) {
    const sourceNode = validatedNodes.find(n => n.name === sourceNodeName);

    if (!sourceNode) {
        console.log(`‚ö†Ô∏è Removing connections from non-existent source: ${sourceNodeName}`);
        removedConnections++;
        continue;
    }

    cleanedConnections[sourceNodeName] = {};

    for (const [connType, connArray] of Object.entries(connectionTypes)) {
        if (!Array.isArray(connArray)) {
            console.log(`‚ö†Ô∏è Invalid connection type format for ${sourceNodeName}.${connType}`);
            continue;
        }

        cleanedConnections[sourceNodeName][connType] = [];

        for (const connGroup of connArray) {
            if (!Array.isArray(connGroup)) {
                console.log(`‚ö†Ô∏è Invalid connection group in ${sourceNodeName}`);
                continue;
            }

            const validConnections = [];

            for (const conn of connGroup) {
                // Validate connection structure
                if (!conn || typeof conn !== 'object') {
                    removedConnections++;
                    continue;
                }

                // Fix missing fields
                if (!conn.node) {
                    console.log(`‚ö†Ô∏è Connection missing target node name`);
                    removedConnections++;
                    continue;
                }

                if (conn.type === undefined) {
                    conn.type = 'main';
                    fixedConnections++;
                }

                if (conn.index === undefined) {
                    conn.index = 0;
                    fixedConnections++;
                }

                // Check if target node exists
                const targetNode = validatedNodes.find(n => n.name === conn.node);
                if (!targetNode) {
                    console.log(`‚ö†Ô∏è Removing connection to non-existent target: ${conn.node}`);
                    removedConnections++;
                    continue;
                }

                validConnections.push(conn);
            }

            if (validConnections.length > 0) {
                cleanedConnections[sourceNodeName][connType].push(validConnections);
            }
        }

        // Remove empty connection types
        if (cleanedConnections[sourceNodeName][connType].length === 0) {
            delete cleanedConnections[sourceNodeName][connType];
        }
    }

    // Remove empty connection objects
    if (Object.keys(cleanedConnections[sourceNodeName]).length === 0) {
        delete cleanedConnections[sourceNodeName];
    }
}

console.log(`üîó Connections: ${removedConnections} removed, ${fixedConnections} fixed`);

// 3. Detect orphaned nodes
const connectedNodeNames = new Set();
for (const [source, types] of Object.entries(cleanedConnections)) {
    connectedNodeNames.add(source);
    for (const connArray of Object.values(types)) {
        for (const group of connArray) {
            for (const conn of group) {
                connectedNodeNames.add(conn.node);
            }
        }
    }
}

const orphanedNodes = validatedNodes.filter(node => 
    !connectedNodeNames.has(node.name) && 
    !node.type.toLowerCase().includes('trigger') &&
    !node.type.toLowerCase().includes('webhook')
);

if (orphanedNodes.length > 0) {
    console.log(`‚ö†Ô∏è Found ${orphanedNodes.length} orphaned nodes: ${orphanedNodes.map(n => n.name).join(', ')}`);
}

// 4. Validate specific node types
const webhookNodes = validatedNodes.filter(n => 
    n.type === 'n8n-nodes-base.webhook' ||
    n.type.toLowerCase().includes('webhook')
);

for (const webhook of webhookNodes) {
    if (!webhook.parameters?.path) {
        console.log(`‚ö†Ô∏è Webhook ${webhook.name} missing path, adding default`);
        if (!webhook.parameters) webhook.parameters = {};
        webhook.parameters.path = `/webhook-${Date.now()}`;
    }
}

// 5. Ensure workflow has a trigger
const hasTrigger = validatedNodes.some(n => 
    n.type.toLowerCase().includes('trigger') ||
    n.type.toLowerCase().includes('webhook')
);

if (!hasTrigger) {
    console.log("‚ö†Ô∏è WARNING: Workflow has no trigger node!");
}

// 6. Final report
const validationReport = {
    total_nodes: validatedNodes.length,
    nodes_fixed: fixedCount,
    connections_removed: removedConnections,
    connections_fixed: fixedConnections,
    orphaned_nodes: orphanedNodes.map(n => n.name),
    webhook_nodes: webhookNodes.map(n => ({ name: n.name, path: n.parameters?.path })),
    has_trigger: hasTrigger,
    validation_passed: validatedNodes.length > 0 && hasTrigger
};

console.log("üìä Validation Report:", JSON.stringify(validationReport, null, 2));

return [{ 
    json: { 
        modify_nodes: validatedNodes,
        modify_connections: cleanedConnections,
        webhook_path: webhook_path,
        n8n_workflow_name : $input.first().json.n8n_workflow_name || '',
        validation_report: validationReport


    } 
}];

const response = $input.first().json;

if (!response.id) {
    throw new Error("‚ùå Workflow creation failed - no ID returned");
}

console.log(`‚úÖ Workflow created:`);
console.log(`   ID: ${response.id}`);
console.log(`   Name: ${response.name || 'N/A'}`);
console.log(`   Nodes: ${response.nodes?.length || 0}`);

return [{ 
    json: { 
        id: response.id,
        name: response.name || 'Unnamed',
        active: response.active || false,
        nodes: response.nodes || [],
        created: true
    } 
}];

const webhookPath = $('Validate & Clean Workflow1').first().json.webhook_path;

if (!webhookPath) {
    console.log("‚ö†Ô∏è Webhook has no path");
    return [{ json: { has_webhook: false, skip_test: true, message: "Webhook missing path" } }];
}

console.log(`‚úÖ Webhook found: ${webhookPath}`);

return [{ 
    json: {
        webhook_path: webhookPath
    } 
}];

console.log('=== Pre-Creation Validator START ===');
const payload = JSON.parse($json.workflowPayload);

const validations = {
  has_nodes: payload.nodes && payload.nodes.length > 0,
  has_connections: payload.connections && Object.keys(payload.connections).length > 0,
  has_webhook: payload.nodes.some(n => n.type === 'n8n-nodes-base.webhook'),
  has_set_data: payload.nodes.some(n => n.name === 'Set Test Data'),
  all_connections_valid: true,
  all_nodes_have_ids: payload.nodes.every(n => n.id),
  all_nodes_have_types: payload.nodes.every(n => n.type),
  all_nodes_have_positions: payload.nodes.every(n => n.position && n.position.length === 2)
};

const nodeNames = new Set(payload.nodes.map(n => n.name));
for (const [source, targets] of Object.entries(payload.connections)) {
  if (!nodeNames.has(source)) {
    validations.all_connections_valid = false;
    console.log(`‚ùå Invalid connection source: ${source}`);
  }
  for (const connArray of Object.values(targets)) {
    for (const connGroup of connArray) {
      for (const conn of connGroup) {
        if (!nodeNames.has(conn.node)) {
          validations.all_connections_valid = false;
          console.log(`‚ùå Invalid connection target: ${conn.node}`);
        }
      }
    }
  }
}

const allValid = Object.values(validations).every(v => v === true);

if (!allValid) {
  console.log('‚ùå Validation failed:', JSON.stringify(validations, null, 2));
  throw new Error(`Pre-creation validation failed: ${JSON.stringify(validations, null, 2)}`);
}

console.log('‚úÖ All pre-creation validations passed');
console.log('=== Pre-Creation Validator END ===');

return $input.all();

